class PomodoroTimer {
    constructor() {
        this.workTime = 25 * 60; // 25 minutes in seconds
        this.breakTime = 5 * 60; // 5 minutes in seconds
        this.timeLeft = this.workTime;
        this.isRunning = false;
        this.isWorkTime = true;
        this.timer = null;

        // DOM elements
        this.timeDisplay = document.getElementById('time');
        this.modeDisplay = document.getElementById('mode');
        this.startButton = document.getElementById('start');
        this.resetButton = document.getElementById('reset');
        this.toggleModeButton = document.getElementById('toggleMode');

        // Event listeners
        this.startButton.addEventListener('click', () => this.toggleTimer());
        this.resetButton.addEventListener('click', () => this.reset());
        this.toggleModeButton.addEventListener('click', () => this.manualSwitchMode());
    }

    toggleTimer() {
        if (this.isRunning) {
            this.pause();
        } else {
            this.start();
        }
    }

    start() {
        this.isRunning = true;
        this.startButton.textContent = 'Pause';
        this.timer = setInterval(() => this.tick(), 1000);
    }

    pause() {
        this.isRunning = false;
        this.startButton.textContent = 'Start';
        clearInterval(this.timer);
    }

    reset() {
        this.pause();
        this.isWorkTime = true;
        this.timeLeft = this.workTime;
        this.updateDisplay();
        this.modeDisplay.textContent = 'Work Time';
    }

    tick() {
        this.timeLeft--;
        
        if (this.timeLeft < 0) {
            this.switchMode();
        }
        
        this.updateDisplay();
    }

    manualSwitchMode() {
        this.pause();
        this.isWorkTime = !this.isWorkTime;
        this.timeLeft = this.isWorkTime ? this.workTime : this.breakTime;
        this.modeDisplay.textContent = this.isWorkTime ? 'Work Time' : 'Rest Time';
        this.toggleModeButton.textContent = this.isWorkTime ? 'Switch to Rest' : 'Switch to Work';
        this.updateDisplay();
    }

    switchMode() {
        this.isWorkTime = !this.isWorkTime;
        this.timeLeft = this.isWorkTime ? this.workTime : this.breakTime;
        this.modeDisplay.textContent = this.isWorkTime ? 'Work Time' : 'Rest Time';
        this.toggleModeButton.textContent = this.isWorkTime ? 'Switch to Rest' : 'Switch to Work';
        
        // Play notification sound and show browser notification
        this.notify();
    }

    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        this.timeDisplay.textContent = 
            `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }

    notify() {
        // Play a beep sound
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
        
        // Show browser notification if supported
        if (Notification.permission === "granted") {
            new Notification(this.isWorkTime ? "Time to work!" : "Time for a break!");
        }
    }
}

// Request notification permission when the page loads
if (Notification.permission === "default") {
    Notification.requestPermission();
}

// Initialize the timer
const pomodoro = new PomodoroTimer(); 